def nb_handles (bigdata)
  puts "Il y a #{bigdata.size} handle dans cette array."
end

def handle_le_plus_court(bigdata)
  bigdata_croissant = bigdata.sort_by{|word| word.length}
  puts "Le handle le plus cours est  #{bigdata_croissant[0]}"
end

def handle_de_5_caracteres(bigdata)
  puts "Le nombre de handle de 5 caractères est : #{bigdata.count {|word| word.length == 6}}"
end

def handle_commence_par_majuscule(bigdata)
  puts "Le nombre de handle qui commencent par une majuscule est : #{bigdata.count {|x| x[1].match?( /[[:upper:]]/ )}}"
end

def tri_ordre_alphabétique(bigdata)
  bigdata_min = bigdata.map(&:downcase)
  bigdata_alpha = bigdata_min.sort
  puts bigdata_alpha
end

def tri_taille_de_handle(bigdata)
  bigdata_croissant = bigdata.sort_by{|word| word.length}
  puts bigdata_croissant
end

def position_epenser(bigdata)
  puts "@epenser est à la position #{bigdata.index "@epenser"} dans l'array"
end

def repartition_par_taille(bigdata)
  15.times do |i|
    puts "Le nombre de handle de #{i+3} caractères est : #{bigdata.count {|word| word.length == i+4}}"
  end
end

def perform(bigdata)
  puts"Bonjour, que souhaitez vous afficher ?"
  puts" - Combien y a-t-il de handle dans cette array ? => tapez 1"
  puts" - Quel est le handle le plus court de cette liste ? => tapez 2"
  puts" - Combien y-a-t'il de handle contenant 5 caractères =>  tapez 3"
  puts" - Combien commencent par une majuscule  ? => tapez 4"
  puts" - Trie la liste de handle par ordre alphabétique. => tapez 5"
  puts" - Trie la liste de handle par taille des handle  => tapez 6"
  puts" - Quelle est la position dans l'array de la personne @epenser ? => tapez 7"
  puts" - Une répartition des handle par taille de ces derniers => tapez 8"
  puts" - tapez 9 pour quitter"

  choix = 0

  while choix != 9
    
    case choix
    when 1
      puts "choix #{choix} :"
      nb_handles(bigdata)
      puts "entrez un nouveau chiffre (9 pour quitter)"
    when 2
      puts "choix #{choix}: "
      handle_le_plus_court(bigdata)
      puts "entrez un nouveau chiffre (9 pour quitter)"
    when 3
      puts "choix #{choix} :"
      handle_de_5_caracteres(bigdata)
      puts "entrez un nouveau chiffre (9 pour quitter)"
    when 4
      puts "choix #{choix} :"
      handle_commence_par_majuscule(bigdata)
      puts "entrez un nouveau chiffre (9 pour quitter)"
    when 5
      puts "choix #{choix} :"
      tri_ordre_alphabétique(bigdata)
      puts "entrez un nouveau chiffre (9 pour quitter)"
    when 6
      puts "choix #{choix} :"
      tri_taille_de_handle(bigdata)
      puts "entrez un nouveau chiffre (9 pour quitter)"
    when 7
      puts "choix #{choix} :"
      position_epenser(bigdata)
      puts "entrez un nouveau chiffre (9 pour quitter)"
    when 8
      puts "choix #{choix} :"
      repartition_par_taille(bigdata)
      puts "entrez un nouveau chiffre (9 pour quitter)"
    when 9
      puts "choix de quitter"      
    else
      puts "Entrez un chiffre entre 1 et 9" 
    end
    choix = gets.chomp.to_i
  end
  puts "merci et bonne journée"
end

perform(bigdata)






